      return {
        ...state,
        stratimuxStatus: stratimuxExists ? ProjectStatus.installed : ProjectStatus.notInstalled,
        logixUXStatus: logixUXExists ? ProjectStatus.installed : ProjectStatus.notInstalled,
        projectsStatuses,
      };
    }
  }
  return {
    ...state
  };
