    actions.push(logixUXServerInnerAddTo({addTo}));
  }
  return createStrategy({
    topic: logixUXServerVerboseAddingStrategyTopic(length),
    initialNode: axium_createGatherNode({actions}),
    data: {
      sum: 0
    }
  });
};

